import "./Contact.scss";

const Contact = () => {
  return (
    <div className="contact">
      <div className="contact__intro">
        <h1 className="contact__heading">Contact Us</h1>
        <p className="contact__description">
          Have questions?<br></br> We're here to help!
        </p>
      </div>
      <form className="contact__form">
        <label htmlFor="name" className="contact__label">
          Name:
        </label>
        <input
          type="text"
          id="name"
          name="name"
          className="contact__input"
          required
        />

        <label htmlFor="email" className="contact__label">
          Email:
        </label>
        <input
          type="email"
          id="email"
          name="email"
          className="contact__input"
          required
        />

        <label htmlFor="order" className="contact__label">
          Order Number (optional):
        </label>
        <input type="text" id="order" name="order" className="contact__input" />

        <label htmlFor="message" className="contact__label">
          Message:
        </label>
        <textarea
          id="message"
          name="message"
          className="contact__textarea"
          required
        ></textarea>

        <button type="submit" className="contact__button">
          Submit
        </button>
      </form>
      <div className="contact__info">
        <h2 className="contact__subheading">Get in Touch</h2>
        <p className="contact__text">
          Email:{" "}
          <a href="mailto:info@shopifystore.com">info@shopifystore.com</a>
        </p>
        <p className="contact__text">Phone: (123) 456-7890</p>
        <p className="contact__text">
          Address: 123 Sticker Lane, Keychain City
        </p>
      </div>
      <div className="contact__socials">
        <h2 className="contact__subheading">Follow Us</h2>
        <a
          href="https://www.facebook.com/shopifystore"
          className="contact__social-link"
          target="_blank"
          rel="noopener noreferrer"
        >
          Facebook
        </a>
        <a
          href="https://www.instagram.com/shopifystore"
          className="contact__social-link"
          target="_blank"
          rel="noopener noreferrer"
        >
          Instagram
        </a>
      </div>
    </div>
  );
};

export default Contact;
