import "./Footer.scss";
import { FaInstagram } from "react-icons/fa";

const Footer = () => {
  return (
    <footer className="footer">
      <div className="footer__container">
        <div className="footer__info">
          <h2 className="footer__subheading">Connect with Rikakuma</h2>
          <p className="footer__text">
            Discover the latest updates, events, and inspiration. Let’s stay connected!
          </p>
          <a
            href="https://www.instagram.com/rikakuma.ca/"
            target="_blank"
            rel="noopener noreferrer"
            className="footer__link"
            aria-label="Visit our Instagram page"
          >
            <FaInstagram className="footer__icon" />
            <span>@rikakuma.ca</span>
          </a>
        </div>
        <div className="footer__copyright">
          <p>© 2025 Rikakuma. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
