import "./Header.scss";
import { useState, memo } from "react";
import logo from "../../assets/images/rikakumalogo.jpeg";
import { FaBars, FaTimes } from "react-icons/fa";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen((prevState) => !prevState);
  };

  return (
    <div className="header">
      <div className="header__container">
        {/* Logo */}
        <img className="header__logo" src={logo} alt="Rikakuma Logo" />

        {/* Hamburger Menu Icon */}
        <div
          className="header__menu-icon"
          onClick={toggleMenu}
          aria-label="Toggle Menu"
        >
          {isMenuOpen ? <FaTimes /> : <FaBars />}
        </div>
      </div>

      {/* Render Navigation Menu Conditionally */}
      {isMenuOpen && (
        <nav className="header__nav header__nav--open">
          <ul className="header__nav-list">
            <li className="header__nav-item">
              <a href="#home" onClick={toggleMenu}>
                Home
              </a>
            </li>
            <li className="header__nav-item">
              <a href="#shop" onClick={toggleMenu}>
                Shop
              </a>
            </li>
            <li className="header__nav-item">
              <a href="#about" onClick={toggleMenu}>
                About
              </a>
            </li>
            <li className="header__nav-item">
              <a href="#contact" onClick={toggleMenu}>
                Contact
              </a>
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
};

export default memo(Header);
